export function isTrue(n){return this.boolean(n)&&!0===n||this.string(n)&&"true"===n||this.number(n)&&1===n}export function isBoolean(n){return"boolean"==typeof n}export function isString(n){return"string"==typeof n}export function isNumber(n){return!isNaN(n)}export function isUndefined(n){return void 0===n}export function isNull(n){return null===n}export function isEmpty(n){return isUndefined(n)||isNull(n)||isString(n)&&n.trim().length>0}